import time as t; WEIGHT = [2, 7, 6, 5, 4, 3, 2]; FG = ["X", "W", "U", "T", "R", "Q", "P", "N", "M", "L", "K"]; ST = ["J", "Z", "I", "H", "G", "F", "E", "D", "C", "B", "A"]; ic = input("Enter IC number: "); ts = t.time(); uic = ic[1:8]; total = int(uic[0]) * WEIGHT[0]+int(uic[1]) * WEIGHT[1]+int(uic[2]) * WEIGHT[2]+int(uic[3]) * WEIGHT[3]+int(uic[4]) * WEIGHT[4]+int(uic[5]) * WEIGHT[5]+int(uic[6]) * WEIGHT[6]; total += 4 if ic[0].lower() in ("t", "g") else 0; remainder = total % 11; print("Valid") if ic[0].lower() in ("f", "g") and ic[-1].lower() == FG[remainder].lower() or ic[0].lower() in ("s", "t") and ic[-1].lower() == ST[remainder].lower() else print("Invalid"); print("Time taken:", (t.time() - ts) * 1000, "ms") # Looped version in luhn_loop.py, Lambda version in luhn_lambda :D ADDED RUN TIME TOO