WEIGHT = [2, 7, 6, 5, 4, 3, 2]; FG = ["X", "W", "U", "T", "R", "Q", "P", "N", "M", "L", "K"]; ST = ["J", "Z", "I", "H", "G", "F", "E", "D", "C", "B", "A"]; ic = input("Enter IC number: "); uic = ic[1:8]; print(uic); total = 0; exec('for i in range(len(uic)): total += int(uic[i]) * WEIGHT[i]'); total += 4 if ic[0].lower() in ("t", "g") else 0; remainder = total % 11; print("remainder", remainder); print("Valid") if ic[0].lower() in ("f", "g") and ic[-1].lower() == FG[remainder].lower() or ic[0].lower() in ("s", "t") and ic[-1].lower() == ST[remainder].lower() else print("Invalid")